INSERT INTO text_responses (category_id, content) VALUES
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}, professionista del non fare niente, esperto/a nel sembrare occupato/a a qualsiasi ora del giorno.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? La risposta courte: qualcuno che occupa spazio senza giustificazione apparente o documentata.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}, nato/a sotto una cattiva stella che però era ubriaca e non stava guardando correttamente.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: conosciuto/a nel quartiere per il talento di essere nel posto sbagliato al momento sbagliato con le parole sbagliate.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, l''unico/a essere vivente capace di complicare persino l''atto di respirare correttamente.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}, una leggenda vivente nel senso che nessuno capisce come faccia a sopravvivere senza assistenza.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Domanda difficile. Anche per {name} stesso/a che si perde allo specchio.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: la risposta alla domanda "cosa succede se si mescola incompetenza totale con sicurezza di sé assoluta?"'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}, il/la campione/ssa mondiale di delusioni reiterate e aspettative non corrisposte.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}, noto/a soprattutto per la capacità di iniziare frasi senza mai finirle e progetti senza mai iniziarli.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco a voi {name}: presenza nota, contributo ignorato, ego talmente sconfinato da avere il proprio codice postale.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name} è quella persona speciale che riesce a essere protagonista di storie in cui non è mai stato/a invitato/a.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Un personaggio secondario che si crede protagonista e non ha capito il copione.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: diploma in stronzaggine, master in autocommiserazione, dottorato in fare danni involontari.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Presentiamo {name}, il/la collega che tutti fingono di non vedere in mensa da almeno tre anni.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name} è quella rara combinazione di confusione mentale totale e certezza assoluta di avere sempre ragione.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, campione/ssa olimpico/a di pensieri inutili espressi sempre nel momento peggiore.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: persona di grande spessore. Purtroppo tutto orizzontale.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Il/la tipo/a che risponde alle email di spam pensando di fare una buona azione per la società.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: dotato/a di una personalità come un foglio A4. Bianco. Non rigato. Non scritto.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}, inventore/trice del niente, brevettatore/trice del vuoto cosmico.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name} è la prova scientifica che l''evoluzione funziona lentamente e a volte si prende una pausa molto lunga.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, l''unica persona capace di litigare con una macchinetta del caffè e avere inequivocabilmente torto.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: conosciuto/a nel settore per cominciare tutto e finire niente. Due hobby, zero risultati, tanta stanchezza.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Il/la tipo/a che dice "non ho voglia" anche per le cose che ama fare da sempre.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}, amato/a da pochi, sopportato/a da molti, evitato/a da tutti quelli abbastanza svegli da farlo.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}: la causa principale del mal di testa di almeno tre generazioni familiari consecutive.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: laurea triennale in procrastinazione, specializzazione in scuse creative, dottorato in mancate promesse.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, il/la tipo/a che porta una bottiglia d''acqua in piscina per paura della sete durante la vasca.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: stratega di professione, risultati di nessuno. Il business plan è un foglio con sopra scritto "poi vediamo".'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Quello/a che in ogni foto di gruppo è mosso/a. Anche nelle foto ferme. Anche nelle foto singole.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: noto/a per rispondere ai messaggi con la stessa tempestività con cui la lumaca corre la maratona di Milano.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}, detentore/trice del record mondiale di occasioni sprecate in ordine cronologico.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: capace di togliere la voglia di vivere anche alle piante grasse più resistenti.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, il/la tipo/a che sbaglia il PIN del bancomat anche con i numeri scritti sulla mano.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: presente in corpo, assente in spirito, latitante in intelligenza. Tre su tre.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Il/la tipo/a che commenta le notizie senza leggerle. E ha sempre torto. Senza eccezioni.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: tecnicamente umano/a, funzionalmente un punto interrogativo con le gambe.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}, il/la campione/ssa di "Ho capito tutto" seguito da prova lampante del contrario immediato.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: quella persona che si presenta alle feste senza essere invitata e poi si lamenta degli antipasti.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, il/la portatore/trice sano/a di caos organizzato in qualsiasi contesto sociale.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: persona di poche parole. Peccato che quelle poche siano sempre fuori luogo e fuori tempo.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? L''unico/a essere vivente capace di sbagliare direzione in un corridoio a senso unico.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: certificato/a immune al sarcasmo. Ogni battuta viene presa alla lettera. Ogni volta. Senza mai imparare.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}: il/la tipo/a che cerca il senso della vita nel menu dei ristoranti.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: non è cattivo/a, è solo ottimizzato/a per il danno collaterale massimo con sforzo minimo.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, il/la genio/a incompreso/a che nessuno ha capito perché oggettivamente c''è poco da capire.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: conosciuto/a per la capacità di trasformare qualsiasi successo in fallimento nel giro di 10 minuti esatti.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Il/la tipo/a che mette le proprie esigenze al primo posto. E al secondo. E al terzo. E così via.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: persona di larghe vedute. Peccato che le vedute siano tutte sistematicamente sbagliate.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}, l''unico/a membro del club "Ho ragione io" con zero soci soddisfatti.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: noto/a per parlare tanto e non dire mai niente che valga la pena ricordare il giorno dopo.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, il/la portatore/trice cronico/a del complesso della vittima in ogni contesto non vittimistico.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: tecnicamente funziona. Nel senso che respira. Il minimo indispensabile.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Quello/a che nella riunione parla per 20 minuti senza arrivare al punto né avvicinarsi.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: ha tutte le caratteristiche del genio incompreso tranne quella fondamentale del genio.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}: il/la collega che scopre sempre di aver scelto il lavoro sbagliato dieci anni dopo.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: noto/a per avere opinioni fortissime su argomenti di cui non sa assolutamente niente.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}: presenza marginale, impatto minimo, ego al massimo storico. Equilibrio precario.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: specializzato/a nel trovare scuse per situazioni in cui bastava semplicemente chiedere scusa.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Il/la tipo/a che sbaglia la pizza quando ordina. Ogni volta. La stessa pizzeria. Da 5 anni.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: persona di successo nella dimensione parallela in cui vive e che nessun altro riesce a vedere.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}: il/la campione/ssa di "Avevo detto io" anche nei casi in cui non l''aveva detto.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: noto/a per essere il motivo per cui esistono le avvertenze sui prodotti elettronici.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, il/la tipo/a che completa le frasi degli altri. Sempre in modo sbagliato. Con sicurezza.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: conosciuto/a localmente, ignorato/a globalmente, sopportato/a per pura cortesia formale.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Quello/a che porta il malocchio alle situazioni positive e buona fortuna a quelle già negative.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: tecnicamente capace di tutto. In pratica incapace di quasi tutto il tempo.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}: il/la tipo/a che manda vocali di 10 minuti per dire una cosa da 10 secondi.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: persona affascinante nel senso che ci si chiede costantemente come faccia a essere così persistentemente sbagliato/a.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}, professore/ssa emerito/a dell''Università del Niente, cattedra di Studi sull''Inutile.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: noto/a per avere un piano B anche per il piano B. Tutti e due falliscono invariabilmente.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Chi è {name}? Il/la tipo/a che dice "vado un secondo" e torna dopo 45 minuti senza spiegazioni.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: persona di grandi risorse personali. Completamente inesplorate. Per scelta deliberata.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Vi presentiamo {name}: il/la tipo/a che legge la fine del libro prima di iniziarlo e poi si lamenta del finale.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: capace di sembrare impegnato/a anche mentre non fa assolutamente niente di niente.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), 'Ecco {name}: il/la tipo/a che porta l''ombrello a Ferragosto e lo lascia a casa quando piove.'),
  ((SELECT id FROM categories WHERE slug = 'chi-e'), '{name}: una storia di vita che potrebbe essere un romanzo. Di quelli che si trovano nel cestino della libreria.');
INSERT INTO text_responses (category_id, content) VALUES
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ah poverino/a! Chiamo l''ambulanza o bastano i vigili del fuoco?'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ti compatisco tantissimo. No aspetta, non è vero.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Eh, capita. Ma sinceramente non mi interessa.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Che tragedia cosmica! Vuoi che avvisi la Croce Rossa internazionale?'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Il tuo dolore mi tocca profondamente. Detto questo, me ne frego completamente.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Dio mio, che situazione terribile. Comunque, qualcuno ha visto il telecomando?'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Il tuo lamento è stato registrato e sarà ignorato entro 3-5 giorni lavorativi.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Tienimi aggiornato/a, ma da molto molto lontano.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Capisco il tuo disagio profondo. No, non è vero neanche stavolta.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Bello il tuo problema. Ne avrò uno simile mai nella vita.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Vuoi un violinista in sottofondo o preferisci piangerti addosso in silenzio dignitoso?'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'La vita è dura. Poi si muore. Buona fortuna nel frattempo.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Mi spezza il cuore. Che però è fatto di marmo per queste occasioni.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Nota la mia faccia di finta preoccupazione. Ci sto lavorando da ore.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Sono qui per te. Da questa parte dello schermo. Lontanissimo/a.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Il tuo problema è validissimo. Validissimamente non mio però.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Che sfortuna, amico/a mio/a. La vita ti vuole talmente bene che ti strozza.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Sto soffrendo per te. No, aspetta: sto sbadigliando. Mi confondo sempre.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Che bella notizia! Ah no, era un lamento. Mi sbaglio sempre con questi.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Hai bisogno di una spalla su cui piangere? Peccato che la mia sia in manutenzione.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ti capisco profondamente. Come non capisco perché continui a lamentarti invece di fare qualcosa.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ehilà! Benvenuto/a nel club "La vita fa schifo". Quota mensile: zero lacrime di coccodrillo.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Il tuo dramma personale è stato registrato nell''apposito cestino rosso dell''indifferenza.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ah, la vita! Sempre così vigliaccamente viva, vero? Che seccatura.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Mi dispiace per te. Ma neanche troppo, per essere completamente onesti.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Grande problema epocale! Hai già chiamato il 112, 113, 115, 118 e Papa Francesco?'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Interessante. Adesso dimmi qualcosa che mi interessa davvero.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Capisco la tua sofferenza e la rispetto... da dietro una porta blindata.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Posso offrirti un momento di silenzio? Soprattutto il tuo silenzio.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Stai male? Prenditi una camomilla e smettila di rompere a tutti.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Sei stanco/a della vita? La vita è ancora più stanca di te, credimi.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Poverino/a! Quanto soffri. Ma soprattutto quanto fai soffrire gli altri con questo piagnisteo.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Hai bisogno di supporto emotivo? Ti mando un cuscino via Amazon Prime, arrivo in 24 ore.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Il tuo disagio è completamente comprensibile. E completamente insopportabile.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ah, ti lamenti ancora! Che novità entusiasmante, eh? Come sempre.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Un consiglio non richiesto: smettila di lamentarti e fai qualcosa. Qualsiasi cosa. Anche alzarti.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Capisco. E con questo ho esaurito la mia empatia giornaliera. Quota finita.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Sei a pezzi? Gliene faccio un paio d''altri così almeno è completo.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'La vita ti ha deluso? Ti ha preso esattamente per quello che sei.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Coraggio! O almeno fai finta convincente. Meglio per tutti i presenti.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Stai soffrendo? Che carattere forte! Peccato che non cambi assolutamente niente.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Empatia mode: ON... nah, troppa fatica. OFF.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'La tua lamentela è stata ascoltata, valutata e classificata: irrilevante totale.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Sei triste? Mettiti una felpa, guarda YouTube e smetti di disturbare gli adulti.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Capisco il tuo stato d''animo. È una cosa tua però, tienilo.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Vorrei consolarti ma non ho il tempo, la voglia o la minima capacità in questo momento.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Dai, su! Potrebbe andare peggio. Probabilmente andrà peggio. Ma adesso potrebbe non farlo.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Il tuo sfogo è durato 3 minuti. La mia pazienza 45 secondi. Siamo matematicamente in debito.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Sai cosa ti farebbe bene? Non spetta a me dirtelo, ma sicuramente non è lamentarti.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Forza, che ce la fai! Ma non insistere troppo, che ce la fai benissimo anche da solo/a.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Quante energie spendi a lamentarti. Immagina se le usassi per qualcosa vagamente utile.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ti do un consiglio: il problema che hai è esattamente tuo. Tienilo, non condividerlo.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Hai il coraggio di lamentarti qui? Rispetto. Mal riposto completamente. Ma rispetto.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Il tuo dramma merita un film. Di quelli che vanno direttamente su Netflix alle 3 di mattina.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Ho registrato la tua sofferenza. L''ho messa in cartella. La cartella è nel cestino.'),
  ((SELECT id FROM categories WHERE slug = 'lamenti'), 'Capisco che tu stia male. Stai male da prima che ti conoscessi. Ci siamo abituati tutti.');
