INSERT INTO categories (slug, name, type, description) VALUES
  ('fact-check', 'Fact Check', 'text', 'Fact check inventati e assurdi'),
  ('complotto', 'Complotto', 'text', 'Teorie del complotto che coinvolgono due utenti del gruppo'),
  ('eredita', 'Eredità', 'text', 'Testamenti e eredità inventate con due utenti del gruppo'),
  ('ricetta', 'Ricetta', 'text', 'Ricette disgustose e impossibili'),
  ('autopsia', 'Autopsia', 'text', 'Referti autoptici inventati e volgari');
